import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";

import CartCard from "../components/cart/CartCard";
import Footer from "../components/default/Footer";
import Header from "../components/default/Header";
import { fetchCarts } from "../reducks/cart/operations";
import { getCarts } from "../reducks/cart/selectors";
import { clearCheckoutOrderErrorAction } from "../reducks/order/actions";

export default function Cart() {
  const dispatch = useDispatch();
  const selector = useSelector((state) => state);
  const carts = getCarts(selector);
  const history = useHistory();
  const isEmpty = carts.results && carts.results.length > 0 ? false : true;

  useEffect(() => {
    dispatch(fetchCarts());
    // eslint-disable-next-line
  }, []);
  return (
    <>
      <Header totalCart={carts.totalCart} />
      <section className="main-wrapper">
        <div className="cart">
          <p className="title">My Cart(s)</p>
          {isEmpty && (
            <>
              <p>
                Cart is empty. Please go to shopping in order to add product to
                cart.
              </p>
              <button onClick={() => history.push("/")} className="custom-btn">
                Go to Shopping
              </button>
            </>
          )}
          <div className="cart-container">
            {!isEmpty &&
              carts.results.map((cart) => (
                <CartCard key={cart.id} cart={cart} />
              ))}
          </div>
          {!isEmpty && (
            // <>
            //   <hr className="cart-line" />
            //   <div className="total-cart">
            //     <div>
            //       <div className="sub-total">
            //         <p>SUBTOTAL:</p>
            //         <p>$ {carts.totalPrice}</p>
            //       </div>
            //       <div className="total-item">
            //         <p>ITEM(S):</p>
            //         <p>{carts.totalCart}</p>
            //       </div>
            //       <button
            //         onClick={() => {
            //           history.push("/checkout");
            //           dispatch(clearCheckoutOrderErrorAction());
            //         }}
            //         className="proceed-checkout"
            //       >
            //         PROCEED TO CHECKOUT
            //       </button>
            //     </div>
            //   </div>
            // </>
            <div className="cartfooter">
              <h4 class="cartcost">
                Total Cost:{" "}
                <span class="subtotal-cost">${carts.totalPrice}</span>{" "}
              </h4>

              <div class="cost">
                <button
                  className="continueshop"
                  onClick={() => history.push("/")}
                >
                  Continue Shopping
                </button>

                <button
                  class="nextstep-button"
                  onClick={() => {
                    history.push("/checkout");
                    dispatch(clearCheckoutOrderErrorAction());
                  }}
                >
                  Next Step
                </button>
              </div>
            </div>
          )}
        </div>
      </section>

      {/* cart for atoz project */}

      <Footer />
    </>
  );
}
