import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useLocation } from "react-router";
import { Link } from "react-router-dom";
import { push } from "connected-react-router";
import Footer from "../components/default/Footer";
import Header from "../components/default/Header";
import { signIn } from "../reducks/users/operations";
import { getUser } from "../reducks/users/selectors";
import Landing from "./Landing";
export default function SignIn() {
  const dispatch = useDispatch();
  const history = useHistory();
  const { search } = useLocation();
  const selector = useSelector((state) => state);
  const errors = getUser(selector).errors;

  const initialValues = {
    email: "",
    password: "",
  };

  const [values, setValues] = useState(initialValues);
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    setValues({
      ...values,
      [name]: value,
    });
  };
  const closeButton = () => {
    dispatch(push("/"));
  };
  const onSubmitSignIn = () => {
    setIsLoading(true);
    dispatch(signIn(values, () => history.push({ pathname: "/", search })));
    setIsLoading(false);
  };
  return (
    <>
      {/* <Header search={search} /> */}
      {/* <section className="main-wrapper">
				<div className="sign-in">
					<p className="title">SIGN IN</p>
					<div className="form-container">
						<label htmlFor="email">Email Address</label>
						<input
							className="custom-input"
							type="email"
							name="email"
							placeholder="Enter Email"
							value={values.email}
							onChange={handleInputChange}
						/>
						{errors.email ? <span className="error-text">{errors.email[0]}</span> : null}
						<label className="mt-2" htmlFor="email">
							Password
						</label>
						<input
							className="custom-input"
							type="password"
							name="password"
							placeholder="Enter Password"
							value={values.password}
							onChange={handleInputChange}
						/>
						{errors.password ? <span className="error-text">{errors.password[0]}</span> : null}
						{errors.error ? <span className="error-text">{errors.error}</span> : null}
						<button className="custom-btn" onClick={onSubmitSignIn}>
							{isLoading ? "SIGNING IN..." : "SIGN IN"}
						</button>
						<p>
							New Customer ? <Link to={{ pathname: "sign-up", search }}>Register</Link>
						</p>
					</div>
				</div>
			</section> */}
      <div className="blur">
        <Landing />
      </div>
      <div class="wrap">
        <div class="login-form">
          <div class="form-header">
            <h3 class="signinbox">Log In</h3>
          </div>

          {/* <!--Email Input--> */}
          <div class="form-group">
            <input
              className="form-input"
              type="email"
              name="email"
              placeholder="Enter Email"
              value={values.email}
              onChange={handleInputChange}
            />
            {errors.email ? (
              <span className="error-text">{errors.email[0]}</span>
            ) : null}
          </div>
          {/* <!--Password Input--> */}
          <div class="form-group">
            <input
              className="form-input"
              type="password"
              name="password"
              placeholder="Enter Password"
              value={values.password}
              onChange={handleInputChange}
            />
            {errors.password ? (
              <span className="error-text">{errors.password[0]}</span>
            ) : null}
            {errors.error ? (
              <span className="error-text">{errors.error}</span>
            ) : null}
          </div>
          {/* <!--Login Button--> */}
          <div class="form-group">
            <button className="form-button" onClick={onSubmitSignIn}>
              {isLoading ? "SIGNING IN..." : "SIGN IN"}
            </button>
          </div>
          <div class="form-footer">
            Not a member yet?
            <a href="/sign-up">Sign Up</a>
          </div>
        </div>
      </div>
      {/* <!--/.wrap--> */}
      <Footer />
    </>
  );
}
